import React, {useState} from 'react'


const Calculator = () => {

const numbers = ['C', '1', '2', '+', '3', '4', '-', '5', '6', '*', '7', '8', '/', '=', '9', '0', '.']

const [inputNum, setInputNum] = useState('');
const onButtonClick = (buttontext) => {
if(buttontext === "C"){
const result1 = '';
setInputNum(result1);
} else if(buttontext === '='){
const result2 = eval(inputNum);
setInputNum(result2);

}else {
    const newDisplayShow = inputNum + buttontext
    setInputNum(newDisplayShow);
}

}
  return (
  
    <div className="calstyle">
    <input className="inputstyle" type="text" readOnly value={inputNum}/>
    <div className="buttonstyle">{
        numbers.map((num) => {
            return(
        <button className="buttons" key={num} onClick={() => onButtonClick(num)}>{num}</button>
    )
        })
    }
    </div>
    </div>
  )
}

export default Calculator
