import React, {useState} from 'react'

const Calculator = () => {
const numbers = ['C', '1', '2', '+', '3', '4', '-', '5', '6', '*', '7', '8', '/', '=', '9', '0', '.']
  
const [inputMethod, setInputMethod] = useState('25');

return (
    <>
   <div className="calstyle">
    <input className="inputstyle" type='text' readOnly value={inputMethod}/>
    <div className="buttonstyle">
    {
        numbers.map((allNumbers)=>{
            return(    
                
     <button className="buttons" >{allNumbers}</button>
     
    )
        })
    }
    </div>
   
   </div>
    </>
  )
}

export default Calculator
