import React, { useCallback, useState, useEffect, useRef } from 'react';

const App = () => {
  const [passlength, setPassLength] = useState(8);
  const [passchar, setPassChar] = useState(false);
  const [passnum, setPassNum] = useState(false);
  const [password, setPassword] = useState('');
  const [copied, setCopied] = useState(false);

  const passwordRef = useRef(null);

  const copyPassword = useCallback(() => {
    passwordRef.current.select();
    window.navigator.clipboard.writeText(password);
    setCopied(true); // Set copied to true after copying
    setTimeout(() => setCopied(false), 2000); // Reset copied after 2 seconds
  }, [password]);

  const passwordGenerator = useCallback(() => {
    let pass = '';
    let str = 'ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz';
    if (passchar) str += '`~!@#$%^&*()-_=+[{]}';
    if (passnum) str += '0123456789'; 
    for (let i = 0; i < passlength; i++) {
      let char = Math.floor(Math.random() * str.length);
      pass += str.charAt(char);
    }
    setPassword(pass);
  }, [passlength, passchar, passnum]);

  useEffect(() => {
    passwordGenerator();
  }, [passlength, passchar, passnum, passwordGenerator]);

  return (
    <>
      <h1 style={{ textAlign: 'center' }}>Password Generator</h1>
      <input type="text" placeholder='Choose the password' value={password} readOnly ref={passwordRef} />
      <button onClick={copyPassword}>Copy</button>
      {copied && <span style={{ color: 'green' }}>Copied!</span>} {/* Display copied message */}
      <input type="range" min={4} max={30} value={passlength} onChange={(e) => setPassLength(parseInt(e.target.value))} />
      <label htmlFor='length'>Length: {passlength}</label>
      <input type="checkbox" checked={passchar} id="numberCharacter" onChange={() => setPassChar((prev) => !prev)} />
      <label htmlFor='numberCharacter'>Characters</label>
      <input type="checkbox" checked={passnum} id="numberInput" onChange={() => setPassNum((prev) => !prev)} />
      <label htmlFor='numberInput'>Numbers</label>
    </>
  );
};

export default App;
