import React, {useState} from 'react'

const ColorPicker = () => {

    const [color, setColor] = useState('#FFA500');
    return(
        <>
        <div>
            <h1>User Color Picker</h1>
            <div>
                <p>Selected Color</p>
            </div>
            <label>Select a color</label>
            <input type='color'/>
        </div>
        </>
    )
}

export default ColorPicker;
