function UpdatedLists(props){

   const color1 = {
    fontSize: '2.5em',
    fontWeight: 'bold',
    color: 'hsl(0, 0%, 20%)',
    marginBottom: '10px',
    textAlign: 'center',
    border: '3px solid',
    borderRadius: '5px',
    backgroundColor: 'hsl(185, 100%, 50%)'
}

    const category = props.category;
    const items = props.itemName;
    const listItems = items.map(item => <li key= {item.id}>{item.name}: &nbsp;<b>{item.price}</b></li>)
    return(
        <>
        <h2 style={color1}>{category}</h2>
     <ol>{listItems}</ol>
        </>
    );
}

export default UpdatedLists
