import React, {useState} from 'react'

const App = () => {

  const [color, setColor] = useState('white');

  return (
    <div style={{minHeight:'100vh'}}>
  <div style={{backgroundColor:color, display:'flex', justifyContent:'center', textAlign:'center'}}>

  <button style={{backgroundColor:'red', color:'white', fontSize:'1.5rem'}} onClick={()=>setColor('red')}>Red</button>
  <button style={{backgroundColor:'green', color:'white', fontSize:'1.5rem'}} onClick={()=>setColor('green')}>Green</button>
  <button style={{backgroundColor:'blue', color:'white', fontSize:'1.5rem'}} onClick={()=>setColor('blue')}>Blue</button>
  </div>
  </div>
  )
}

export default App
