import React, {useState} from 'react';
import Tdatas from './Tdatas.json';

const Tdata = () => {

const [nData,setnData] = useState(Tdatas);

const handleclick = () => {

    setnData([]) 
}
return(
<>
<ul>
{

nData.map(items => 
<li key = {items.id}>
 {items.name} 
<br/>
 <button>Remove</button>  
</li>)
}
</ul>
<button onClick = {handleclick}>Clear All</button>
</>
)

}
export default Tdata
