import React from 'react'
import PropTypes from './prop-type'
const color1={
    fontSize: "2.5em",
    backgroundColor: "hsl(120, 100%, 42%)",
    color: "white",
    padding: "10px",
    borderRadius: "5px",
    margin: "0",
}


const color2={
    fontSize: "2.5em",
    backgroundColor: "hsl(0, 100%, 42%)",   
    color: "white",
    padding: "10px",
    borderRadius: "5px",
    margin: "0",
}



const UserGreetings = (props) => {
    
    const user1 = <p style={color1}>Welcome {props.username}  </p> 

   const user2 = <p style={color2}>please login first...</p>
    /* if(props.isLoggedIn){

        return <p style={color1}>Welcome {props.username}</p>
        
         }else{
            return <p style={color2}>please login first...</p>
            
         } */

       /*  return(
      props.isLoggedIn ?   <p style={color1}>Welcome {props.username}  </p> : 
                           <p style={color2}>please login first...</p>
        ) */

        return(
       props.isLoggedIn ? user1 : user2
        )
}

UserGreetings.proptypes = {
    isLoggedIn: PropTypes.bool,
    username: PropTypes.string,
}

UserGreetings.defaultProps={
    isLoggedIn:false,
    username:'Guest'
}
export default UserGreetings
