import React, {useState} from 'react'
import NewComp from './NewComp';

const Memo = () => {
const[count,setCount] = useState(0);
const[input,setInput] = useState('');
const handleInput = (e)=>{
setInput(e.target.value)
}

  return (
    <div>
      <h1>{count}</h1>
      <button onClick={()=>setCount(count+1)}>Incre Value</button><br/>
  <br/>

      <input type= 'text' value={'/'} onChanges={handleInput}/>
      <hr/>
      <NewComp newCount={count}/>

     </div>
  )
}

export default Memo
