import "./Home.css";
import React from 'react';
const Mobile = (props) => {
    
    const image = "https://rukminim2.flixcart.com/image/312/312/xif0q/mobile/4/h/e/-original-imagzjhwmsamexfk.jpeg?q=70";
    const title = "vivo T2x 5G";
    const brand = "By Vivo";
    const price = "1000";
    return (
   
        <div className="mobile-specs">
           
            <div className="mobile-image"><img src= {image}/></div>
            <div className = "mobile-info"><h2>{title}</h2>
          <p className= "mobile-brand">{brand}</p>
           <p className= "mobile-price">${price}</p>
          <button>Buy Now</button>
            </div>
        </div>
    )
}

export default Mobile;
