import React from 'react';

const currDate = new Date().toLocalDateString();
const currTime = new Date()

function Date(){
return(
<>
<h1>Can you tell me date & time?</h1>
<p>The current date is: {currDate}</p>
<p>The current time is: {currTime}</p>
</>
)
}

export default Date;
