import React, { useState } from 'react'

const RemoveEle = () => {
const CbioData = [
{
id: 1, name: 'John', age: 30
},
{
id: 2, name: 'Smith', age: 28

},
{
id: 3, name: 'Henry', age: 26

}

]

const [ClearArr, setClearArr] = useState(CbioData);

const delData = () => {
setClearArr([]);
}

const removeEL = (id) =>{

    const myNewData = ClearArr.filter((curEle) =>{
        curEle.id ==! id
    })
    setClearArr(myNewData);
}
return (
<>
{
ClearArr.map((curEle) => {
return (
<>
<h1 key={curEle.id}>Customer ID: {curEle.id} & Customer Name: {curEle.name} & Customer Age: {curEle.age}</h1>
<button onClick={() => removeEL(curEle.id)}>Remove</button>
</>
)

})
}
<button onClick={delData}>Clear All</button>

</>
)

}
export default RemoveEle
