import PropTypes from 'prop-types'
function UpdatedLists(props){

   const color1 = {
    fontSize: '2.5em',
    fontWeight: 'bold',
    color: 'hsl(0, 0%, 20%)',
    marginBottom: '10px',
    textAlign: 'center',
    border: '3px solid',
    borderRadius: '5px',
    backgroundColor: 'hsl(185, 100%, 40%)'
}

const color2= {

    fontSize: '2em',
    listStyle: 'none',
    color: 'hsl(0, 0%, 25%)',
    textAlign: 'center',
    margin: '0',

}

    const category = props.category;
    const items = props.itemName;
    const listItems = items.map(item => <li key= {item.id}>{item.name}: &nbsp;<b>{item.price}</b></li>)
    return(
        <>
        <h2 style={color1}>{category}</h2>
     <ol style={color2}>{listItems}</ol>
        </>
    );
}

UpdatedLists.propTypes = {
    category: PropTypes.string,
    itemName: PropTypes.arrayOf(PropTypes.shape({  id: PropTypes.number,
                                                name: PropTypes.string,
                                                price: PropTypes.number})),
}
UpdatedLists.defaultProps = {
    itemName: [],
    category: 'Out of Stock'
}

export default UpdatedLists
