import React, { useState } from "react";

const Home = () => {

const[name, setName] = useState("");

const[firstName, setFirstName] = useState("");

const[name1,setName1] = useState("");

const[lastName,setLastName] = useState("");

const inputcg = (event) =>{
setName(event.target.value);
}

const btnclick = (e) =>{
e.preventDefault();
setFirstName(name);
setLastName(name1);
}

const inputcg2 = (e) =>{
setName1(e.target.value);
}
return (
<>
<form onSubmit={btnclick}>

<h1>Hello {firstName} {lastName}</h1>

<input type= "text" placeholder= "Enter Your First Name" onChange= {inputcg} value={name} />
<br/>

<input type= "text" placeholder= "Enter Your Last Name" onChange= {inputcg2} value={name1}/>
<br />
<button type= "submit">Show more</button>

</form>
</>

);
};

export default Home
