import React, { useState } from "react";

const App = () => {

    const [num, setNum] = useState();
    return (
        <>
            <h1>You choose {num} number</h1>
            <select value={num} onChange={(event) => {
                setNum(event.target.value);
            }}>

                <option value="101">101</option>
                <option value="201">201</option>
                <option value="301">301</option>
                <option value="401">401</option>

            </select>
        </>
    )
}

export default App

const obj = new Promise((resolve, reject) => {

    setTimeout(() => {
       const EmpTN = [2, 4, 6, 8]
        resolve(EmpTN)
        reject("Error found");
    }, 2000)

});

const getData = (num) => {
return new Promise((resolve, reject) => {
    setTimeout((num) => {
        const bioData = {
            name: 'saurav',
            age: 25,
            salary: '1,50000'
        }
    
        resolve(`Employer name is ${bioData.name}, token number ${num}, age ${bioData.age} & salary ${bioData.salary}.`)
    }, 2000);
})
}

obj.then((data)=> {

    console.log(data)
});getData(EmpTN[2]).then((data)=>{
console.log(data);
}).catch((error)=>{
    console.log(error);
});
