import react, {useEffect, useState} from 'react'

const UseEffect =()=>{
    
const[clicked,setClicked] = useState();
const[count,setCount] = useState(0);
useEffect(()=>{
alert('we are in homepage')
}, [count]);
   

return(
<div>
<button onClick={()=>setClicked('RR')}>Follow</button><br/><hr/>
<button onClick={()=>setClicked('IA')}>us on</button><br/><hr/>
<button onClick={()=>setClicked('IN')}>Social Media</button><hr/>
<h1>{clicked}</h1>
<button onClick={()=> setCount(count +2)}>Increment</button>
<h1>{count}</h1>
</div>
)
}

export default UseEffect
