import React, {useState} from 'react';

const ButtonOnclick = () => {
 const [count, setCount] = useState(0);

const buttonClick1 = () => {
return(
setCount(count+1)
)

}

const buttonClick2 = () => {
return(
setCount(count-1)
)
}
return(
<>
<h1>{count}</h1>
<button onClick= {buttonClick1}>+</button>
<button onClick= {buttonClick2}>-</button>
</>

)
}

export default ButtonOnclick
