import React from 'react'

const ArrayMethod = () => {

const fruits = [
    {id: 1, name: 'apple', calories: 40},
    {id: 2, name: 'mango', calories: 50},
    { id: 3, name: 'banana', calories: 80},
    { id: 4, name: 'pineapple', calories: 70},
    { id: 5, name: 'orange', calories: 60}
]
// fruits.sort((a, b) => a.name.localeCompare(b.name)); // SORT ALPHABETICAL
// fruits.sort((a, b) => b.name.localeCompare(a.name)); // REVERSE ALPHABETICAL

// fruits.sort((a, b) => a.calories - b.calories); // SORT BY CALORIES (NUMERIC)
// fruits.sort((a, b) => b.calories - a.calories); // REVERSE SORT BY CALORIES
   

const listItems = fruits.map(fruit => <li key={fruit.id}>{fruit.name}:&nbsp;
                                    <b>{fruit.calories}</b></li>);

console.log(fruits)
  return (
    <>
 <ol>{listItems}</ol>
    </>
    
  )
}

export default ArrayMethod
