import React, { useState } from 'react'

const UpdateData = () => {

    const [ValUpdate, setValUpdate] = useState({ myName: 'Henry', myAge: 25, myLocation: 'California', myHobby: 'Cricket' }
    );

    const UpdateVal = () => {
        setValUpdate({...ValUpdate, myName: 'Saurav', myLocation: 'Mumbai'});
    }
    return (
        <>
            <h1>Name: {ValUpdate.myName} & Age: {ValUpdate.myAge} & Location:{ValUpdate.myLocation} & Hobby: {ValUpdate.myHobby}</h1>
            <button onClick={UpdateVal}>Update Current Data</button>
        </>
    )
}

export default UpdateData
