import React,{useState, useEffect, useCallback, useRef} from 'react'

const App = () => {

const [passlength, setPassLength] = useState(2)
const [numpassword, setNumPassword] = useState(false)
const [charpassword, setCharPassword] = useState(false)
const [password, setPassword] = useState('')
const [copied, setCopied] = useState(false)

const PasswordRef = useRef(null);
const copyPassword = useCallback(() =>{
  PasswordRef.current.select();
  window.navigator.clipboard.readTextText(password)
  setCopied(true);
  setTimeout(() => 
setCopied(false), 2000)}, [password])

const PasswordGen = useCallback(() => {
let pass = ''
let strng = 'ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz'
if(numpassword) strng+='0123456789'
if(charpassword) strng+= "`~!@#$%^&*()-_=+[{]}|;:',<.>/?"

for(let i=0; i<passlength; i++){
  let randompass = Math.floor(Math.random() * strng.length)
  pass +=strng.charAt(randompass);
}
setPassword(pass);

}, [passlength, numpassword,charpassword])

useEffect(() => {
  PasswordGen();
}, [passlength, numpassword,charpassword, PasswordGen])
  return(

    <div style={{textAlign:'center'}}>
    <h1>Password Generator</h1>
    <input type="text" value={password} placeholder='choose your password' readOnly ref={PasswordRef} />
    <button onClick={copyPassword}>Copy Password</button>
    {copied && <span style={{color:'green'}}>Copied Text</span>}
    <input type="range" min={3} max={30} value={passlength} id="PasswordLength" onChange={(e) => setPassLength(e.target.value)}/>
    <label htmlFor="PasswordLength">Password Length: {passlength}</label>
    <input type='checkbox' id="NumberPassword" checked={numpassword} onChange={() => setNumPassword((prev)=> !prev)}/>
    <label htmlFor="NumberPassword">Number</label>
    <input type='checkbox' id="CharacterPassword" checked={charpassword} onChange={() => setCharPassword((prev) => !prev)}/>
    <label htmlFor="CharacterPassword">Character</label>
    </div>
  )
}

export default App
