import React, { useState } from 'react'

const UseEffect = () => {

    const[num, setNum] = useState(0);
    const addCount = () => {
        setNum(num + 1);

    }
  return (
    <>
    <h1>Count Number: {num}</h1>
    <button onChange={addCount}>Increment</button>
    </>
  )
}

export default UseEffect
