import React, { useState } from "react";

const Home = () => {

    const [fullName, setFullName] = useState()


    const inputcg = (e) => {
        setFullName(e.target.value)
    }

    const btnclick = (e) => {
        e.preventDefault();
alert("Form Submitted");
    }

    return (
        <>
            <form onSubmit={btnclick}>

                <h1>Hello: {fullName}</h1>

                <input type="text" placeholder="Enter Your First Name" onChange={inputcg} name="firstName" />
                <br />

                <input type="text" placeholder="Enter Your Last Name" onChange={inputcg} name="lastName"  />
                <br />
                <button type="submit">Show more</button>

            </form>
        </>

    );
};

export default Home
