const handleInput= (e)=>{
setInput(e.target.value)
}

function slowFunc(){
let sum = 0;
  for(let i=0; i<=1000; i++){
    sum+=i;
  }

console.log("This is slow func")
}
slowFunc();
  return (
    <div>
      <h1>{count}</h1>
      <button onClick={()=> setCount(count+2)}>Value</button><br/><br/>
      <input type='text' value={input} onChange={handleInput}/>
      <h2>Input: {input}</h2>
    </div>
  )
}

