import React, { useEffect, useState } from 'react'

const EffectAndState = () => {
    const [count, setCount] = useState(0);

    useEffect(()=>{
        if(count>=1){
        document.title = `Chat ${count}`
        }else{
            document.title = `Chat`  
        }
       
    }, [count]);
  return (
    <>
    <h1>{count}</h1>
    <button onClick={() => {setCount(count+1)}}>Click me</button>
    </>
  )
}

export default EffectAndState
