import React, { useState } from "react";

const Home = () => {

    const[name, setName] = useState();
    const[bname, setbName] = useState();

    const inputcg = (event) =>{
setName(event.target.value);
    }

    const btnclick = () =>{
setbName(name);
    }
  return (
<>
<h1>Hello {bname}</h1>
<input type= "text" placeholder= "Enter Your Name" onChange= {inputcg} value= {name}/>
<button type= "button" onClick={btnclick}>Show more</button>
</>

    )
}

export default Home
