import React, {useState} from 'react'

const UseCallback = () => {
  const[count,setCount] = useState(0);
  const[input, setInput] = useState();
const handleInput= (e)=>{
setInput(e.target.value)
}

const result= useCallback(function slowFunc(){
  let sum = 10;
    for(let i=0; i<=10; i++){
      sum+=i;
    }
  
  console.log("This is slow func")
  return sum;
  },[count])

  return (
    <div>
      <h1>{count}</h1>
      <button onClick={()=> setCount(count+2)}>Value</button><br/><br/>
      <input type='text' value={input} onChange={handleInput}/>
      <h2>Input: {input}</h2><br/>
      <hr/>
      {result}
    </div>
  )
}

export default UseCallback
