import React, { useState } from 'react'

const TextChange = () => {
const [TextCg, setTextCg] = useState('Today is Sunday');

const CgText = () =>{
    setTextCg('Tomorrow is Monday');
}

  return (
    <>
    <h1>{TextCg}</h1>
    <button onclick={CgText}>Check the days</button>
    </>
  )
}

export default TextChange
