import React, {useState} from 'react';

const App = () => {
const [count, setCount] = useState(0);

const buttonClick1 = () => {
return(
setCount(prevCount => prevCount + 1)
)

}

const buttonClick2 = () => {
return(
setCount(prevCount => prevCount - 1)
)
}

const btn = () => {
  return(
  setCount()
  )
  }

return(
<>
<h1>count: {count}</h1>
<button onClick= {buttonClick1}>+</button>
<button onClick= {buttonClick2}>-</button>
<button onClick={btn}>Reset</button>
</>

)
}

export default App
