import React, {useState} from 'react'

const Form = () => {
const[name,setName] = useState('')
    const handleName = (e) => {
        console.log(e.target.value)

        setName(e.target.value)
    }
    return(
<>
<label>
Name<br/>
<input type= 'text' value= {name} onChange = {handleName}/>
</label>
</>

    )
}

export default Form
