import React, { useState } from 'react'

const ClearArrData = () => {
const CbioData = [
  {
id: 1, name: 'John', age: 30
  },
  {
    id: 2, name: 'Smith', age: 28

  },
  {
    id: 3, name: 'Henry', age: 26

  }

]

const [ClearArr, setClearArr] = useState(CbioData);

const delData = () => {

  let value = ClearArr;
  if(value===CbioData){
    setClearArr([]);
  }else{
    setClearArr(value);
  }
}
  return (
<>
{
ClearArr.map((cEle) => {
  return <h1>Customer ID: {cEle.id} & Customer Name: {cEle.name} & Customer Age: {cEle.age}</h1>
})
}
<button onClick={delData}>Clear All</button>

</>
  )

  }
export default ClearArrData
