import React, { useState, useEffect } from 'react'

const UseEffect = () => {

    const[num, setNum] = useState(0);
    const [color, setColor] = useState('green');

    useEffect(()=>{
        document.title = `Count Number is: ${num} ${color}`
    }, [num, color]);

    const addCount = () => {
        setNum(n => n + 1);
    }  

    const sumCount = () => {
        setNum(n => n - 1);
    }

    const colorChange = () =>{
        setColor(c => c === 'green' ? 'red' : 'green');
    }
    
  return (
    <>
    <h1 style={{color}}>Count Number: {num}</h1>
    <button style={{color: 'yellow'}} onClick={addCount}>Increment</button>
    <button onClick={sumCount}>Increment</button>
    <button onClick={colorChange}>Color Change</button>
    </>
  )
}

export default UseEffect
