import PropTypes from 'prop-types'

function Student(props){

    const studentName ={
        fontFamily: "Arial, sans-serif",
        fontSize: "2em",
        padding: "10px",
        border: "1px solid hsla(0, 0%, 50%, 0.8"
    }
    const studentPara = {
        margin: "0px"
    }

return(
        
<div style={studentName}>

<p style={studentPara}>Name: {props.name}</p>
<p style={studentPara}>Age: {props.age}</p>
<p style={studentPara}>Student: {props.IsStudent ? "Yes" : "No"}</p>

</div>

    )
    
}

Student.PropTypes ={
name: PropTypes.string,
age: PropTypes.number,
IsStudent: PropTypes.bool,

}
Student.defaultProps = {
    name: "Guest",
    age: "NA",
    isStudent: false,
};
export default Student;
