import React, { useEffect, useState } from 'react'

const CovidData =() => {

  const [covidData, setCovidData] = useState([]);
  const getCovidData = async () =>{
    const response = await fetch('https://data.covid19india.org/data.json');
    const NewData = await response.json();
    setCovidData(NewData.statewise);
  }

  useEffect(()=>{
getCovidData();
  }, [])
  return (
   <>
   <h2>National Covid Data</h2>
   <table>
    <thead>
      <tr>
        <th>State</th>
        <th>Active</th>
        <th>Confirmed</th>
        <th>Deaths</th>
        <th>Recovered</th>
        <th>LastUpdatedTime</th>

      </tr>
    </thead>
    
    <tbody>
    {
      covidData.map((newdata, i)=>{
        return(
<tr>
        <td key={i}>{newdata.state}</td>
        <td>{newdata.active}</td>
        <td>{newdata.confirmed}</td>
        <td>{newdata.deaths}</td>
        <td>{newdata.recovered}</td>
        <td>{newdata.lastupdatedtime}</td>
      </tr>
        )
      })
    }
      
    </tbody>
   </table>
   </>
  )
}

export default CovidData
