import React, {useState} from 'react';
const ButtonOnclick = () =>{
    const [count, setcount] = useState(0);
console.log(setcount);
const handleclickin= () => {
return(
setcount(count+1)
)
}

const handleclickde= () => {
return(
setcount(count-1)
)
}

return(
<>
<h1>{count}</h1>
<button onClick= {handleclickin}>+</button>
<button onClick= {handleclickde}>-</button>
</>
)
}

export default ButtonOnclick
