import React, { useState } from 'react'

const UpdateData = () => {

 const [ValUpdate, setValUpdate] = useState({myName: 'Henry', myAge: 25},
 );

 const UpdateVal = () => {
    setValUpdate({myName: 'Hen', myAge: 25});
 }
  return (
<>
<h1>Name: {ValUpdate.myName} & Age: {ValUpdate.myAge}</h1>
<button onClick={UpdateVal}>Update Current Data</button>
</>
  )
}

export default UpdateData
