import React, {useState} from 'react'

function MyComp(){

const [name, setName] = useState('');
const [quantity, setQuantity] = useState('');
const [comment, setComment] = useState('');
const [payment, setPayment] = useState('');



function changeName(event){

 setName(event.target.value); 
      
}

function changeQuantity(event){
    setQuantity(event.target.value);
}

function changeComment(event){
    setComment(event.target.value);
}

function changePayment(event){
    setPayment(event.target.value);
}
    return(
        <>
        <input type="text" value={name} onChange={changeName}/>
        <p>Name: {name}</p>
        <hr/>
      <input type='number' value={quantity} onChange={changeQuantity}/>
        <p>Quantity: {quantity}</p>
        <hr/>
        <textarea value={comment} onChange={changeComment} placeholder="Enter Description"/>
        <p>Comment: {comment}</p>
        <select value={payment} onChange={changePayment} >
        <option value=''>Select Option</option>
        <option value='PhonePe'>PhonePe</option>
        <option value='Paytm'>Paytm</option>
        <option value='GPay'>GPay</option>
        </select>
        </>
    )
}

export default MyComp;
