import React, {useState} from 'react'

const MyComp = () => {
const [fruits, setFruits] = useState(['apple', 'banana', 'mango']);

const addFruits = () =>{
const newFruits = document.getElementById('fruitItems').value;
document.getElementById('fruitItems').value="";
setFruits(f => [...f, newFruits]);

function removeFood(index){
        
    setFruits(fruits.filter((_, i) => i !== index));
}

}
  return (
    <div>
        <h2>Lists of Fruits</h2>
        <ol>
        {
        fruits.map((fruit, index) => <li key={index} onClick={() => removeFood(index)}>{fruit}</li>)
        }
        </ol>
        <input type="text" id="fruitItems" placeholder="Add Favorite Fruits"/>
        <button onClick={addFruits}>Add Food</button>
    </div>
  )
}

export default MyComp
