import React, { useState } from "react";

const Home = () => {

    const [fullName, setFullName] = useState({
        fname: "",
        lname: "",
        email: "",
        phone: ""
    });


    const inputcg = (e) => {


        const value = e.target.value;
        const name = e.target.name;

        setFullName((prevalue) => {
            if (name === 'firstName') {
                return {
                    fname: value,
                    lname: prevalue.lname,
                    email: prevalue.email,
                    phone: prevalue.phone,
                }
            } else if (name === 'lastName') {
                return {
                    fname: prevalue.fname,
                    lname: value,
                    email: prevalue.email,
                    phone: prevalue.phone,
                }
            }
            else if (name === 'email') {
                return {
                    fname: prevalue.fname,
                    lname: prevalue.lname,
                    email: value,
                    phone: prevalue.phone,
                }
            }
            else if (name === 'phone') {
                return {
                    fname: prevalue.fname,
                    lname: prevalue.lname,
                    email: prevalue.email,
                    phone: value,
                }
            }
        })

    }

    const btnclick = (e) => {
        e.preventDefault();
        alert("Form Submitted");
    }

    return (
        <>
            <form onSubmit={btnclick}>

                <h1>Hello: {fullName.fname} {fullName.lname}</h1>

                <input type="text" placeholder="Enter Your First Name" onChange={inputcg} name="firstName" value={fullName.fname} />
                <br />

                <input type="text" placeholder="Enter Your Last Name" onChange={inputcg} name="lastName" value={fullName.lname} />
                <br />

                <input type="email" placeholder="Enter Your Email Address" onChange={inputcg} name="email" value={fullName.email} />
                <br />

                <input type="number" placeholder="Enter Your Phone Number" onChange={inputcg} name="phone" value={fullName.phone} />
                <br />

                <button type="submit">Show more</button>

            </form>
        </>

    );
};

export default Home
