import React, { useEffect, useState } from "react";


const App = () => {

const [num,setNum] = useState(0);
const [nums,setNums] = useState(0);


useEffect(()=>{
alert('you have clicked')
})
  return (
    <>
<button onClick={()=>{setNum(num + 1)}}>Show more {num}</button>
<br/>
<button onClick={()=>{setNums(nums + 1)}}>Show more {nums}</button>
</>
    )
}

export default App
