import React, { useState } from 'react';

const Calculator = () => {
    const numbers = ['C', '1', '2', '+', '3', '4', '-', '5', '6', '*', '7', '8', '/', '=', '9', '0', '.']

  const [inputNum, setInputNum] = useState('');

  const onButtonClick = (buttonText) => {
    if (buttonText === 'C') {
      setInputNum('');
    } else if (buttonText === '=') {
      try {
        const result = eval(inputNum);
        setInputNum(result.toString());
      } catch (error) {
        setInputNum('Error');
      }
    } else {
      setInputNum((prevInput) => prevInput + buttonText);
    }
  };

  return (
    <div className="calstyle">
      <input className="inputstyle" type="text" readOnly value={inputNum} />
      <div className="buttonstyle">
        {numbers.map((num) => (
          <button className="buttons" key={num} onClick={() => onButtonClick(num)}>
            {num}
          </button>
        ))}
      </div>
    </div>
  );
};

export default Calculator;
