import React from 'react';

function App() {
    const bioData = [
        { name: 'Jack', age: 24, language: 'English', address: [{ Hno: 13, city: 'Mumbai', state: 'Maharashtra' }, { Hno: 14, city: 'Dehradun', state: 'Uttrakhand' }, { Hno: 15, city: 'Mohali', state: 'Punjab' }] },
        { name: 'James', age: 26, language: 'Russian', address: [{ Hno: 14, city: 'Mumbai', state: 'Maharashtra' }, { Hno: 14, city: 'Dehradun', state: 'Uttrakhand' }, { Hno: 15, city: 'Mohali', state: 'Punjab' }] },
        { name: 'Rostov', age: 28, language: 'Spanish', address: [{ Hno: 15, city: 'Mumbai', state: 'Maharashtra' }, { Hno: 14, city: 'Dehradun', state: 'Uttrakhand' }, { Hno: 15, city: 'Mohali', state: 'Punjab' }] }
    ];

    const border = {
        border: '2px solid'
    }
    return (
        <>
            <h1>Nested List With Nested Array</h1>
            <table style={border}>
                <thead>
                    <tr>
                        <th>Name</th>
                        <th>Age</th>
                        <th>Language</th>
                        <th>Address</th>
                    </tr>
                </thead>
                <tbody>
                    {bioData.map((data, index) => (
                        <tr key={index}>
                            <td>{index+1}</td>
                            <td>{data.name}</td>
                            <td>{data.age}</td>
                            <td>{data.language}</td>
                            <td>
                                <ul>
                                    {data.address.map((addr, i) => (
                                        <li key={i}>
                                            {addr.Hno}, {addr.city}, {addr.state}
                                        </li>
                                    ))}
                                </ul>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </>
    );
}

export default App;
