import React, { useState } from 'react'

const ClearArrData = () => {
const bioData = [
{
id: 0, name: 'SS', age: 20
},
{
  id: 1, name: 'GG', age: 22
  }
]
console.log(bioData);

const [Clear, setClear] = useState(bioData);

const RemoveData = () => {
  setClear([]);
}
  return (
<>
{
  Clear.map((CurrEle) => {
return <h1>ID: {CurrEle.id} & Name: {CurrEle.name} & Age: {CurrEle.age}</h1>
  }
  )
}
<button onClick={RemoveData}>Clear</button>
</>  
)
}

export default ClearArrData
