import React from 'react';
//we've to create greeting formats
//if time is between 1Am to 12pm say goodmorning
//if time is between 12pm to 7pm say good afternoon
//if time is between 7pm to 1am say good night

let currDate= new Date().getHours();
let greet="";
let heading={

};
const Greeting = () => {
    
if(currDate>=1 && currDate<12){
greet= "Good Morning";
heading.color='red'
}
else if(currDate>=12 && currDate<=19){
greet= "Good Evening";
heading.color='green'
}
else{
greet = "Good Night";
heading.color='orange'

}

  return (
    <>
    <h1>Hello Sir, <span style={heading}>{ greet }</span></h1>
    </>
  )
}

export default Greeting
