import React, { useState } from 'react';

const BasicLoginForm = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [allEntry, setAllEntry] = useState([]);

  const submitForm = (e) => {
    e.preventDefault();

    if(email && password){

    const newEntry = { email: email, password: password };
    setAllEntry([...allEntry, newEntry]);
    setEmail(''); // Reset email input after form submission
    setPassword(''); // Reset password input after form submission
    }else{
      alert('Fill All Details');
    }
  }


  return (
    <>
      <form onSubmit={submitForm}>
        <label htmlFor='email'>Email</label>
        <input
          type='text'
          name='email'
          id='email'
          value={email}
          onChange={(e) => setEmail(e.target.value)}
        /><br />

        <label htmlFor='password'>Password</label>
        <input
          type='password'
          name='password'
          id='password'
          value={password}
          onChange={(e) => setPassword(e.target.value)}
        />
        <br />

        {/* Move the submit button inside the form */}
        <button type="submit">Login</button>
      </form>

      <br />
      <br />

      {/* Display entered email and password entries */}
      {allEntry.map((curEle, Index) => {
        const {email, password} = curEle
        return (
          <div key="Index">
            <p>Email: {email}</p>
            <p>Password: {password}</p>
          </div>
        );
      })}
    </>
  );
};

export default BasicLoginForm;
