import React, { useEffect, useRef } from 'react'

const App = () => {

  const inputRef = useRef(null);
  const handleChange = () => {
    inputRef.current.focus();
    inputRef.current.style.backgroundColor = "yellow"
  }

  return(
    <>
    <button onClick={handleChange}>Click me</button>
    <input ref={inputRef}/>
    </>
  )
}

export default App
