import React, { useEffect } from 'react'
import "./App.css";

const getCovidData = async() =>{

  try{
    const response = await fetch ('https://data.covid19india.org/data.json');
const actualData = await response.json();
console.log(actualData.statewise[0])
  }

  catch(error){
console.log(error);
  }
}



const App = () => {

  useEffect(() =>{
    getCovidData()
  },[])

  return (
<>
<div className='hero'>
<h1>Live Covid Data</h1>
<h2>Track Covid Infection Data</h2>
</div>
<br/>
<div className='hero1'>
<p className='hero2'>India</p>
<p className="hero3">Active Cases</p>
<p className='hero4'>Recovered from Covid</p>
<p className='hero5'>Total Deaths</p>
<p className='hero6'>Last Update Time</p>
</div>
</>
  )
}

export default App
