    const NewData = await response.json();
  }

  useState(()=>{
getCovidData();
  }, [])
  return (
   <>
   <h2>National Covid Data</h2>
   <table>
    <thead>
      <tr>
        <th>State</th>
        <th>Active</th>
        <th>Confirmed</th>
        <th>Deaths</th>
        <th>Recovered</th>
        <th>LastUpdatedTime</th>


      </tr>
    </thead>
   </table>
   </>
  )
}

