import React, { useState } from "react";

const Home = () => {

    const [fullName, setFullName] = useState({
fname: "",
lname: "",

    });


    const inputcg = (e) => {
console.log(e.target.name);
console.log(e.target.value);

const value = e.target.value;
const name = e.target.name;

setFullName((prevalue)=>{
    if(name==='firstName'){
        return{fname: value,
        lname: prevalue.lname,
    }
    }else if(name==='lastName'){
        return{fname: prevalue.fname,
        lname: value,
    }
    }
})

    }

    const btnclick = (e) => {
        e.preventDefault();
alert("Form Submitted");
    }

    return (
        <>
            <form onSubmit={btnclick}>

                <h1>Hello: {fullName.fname} {fullName.lname}</h1>

                <input type="text" placeholder="Enter Your First Name" onChange={inputcg} name="firstName" value={fullName.fname} />
                <br />

                <input type="text" placeholder="Enter Your Last Name" onChange={inputcg} name="lastName" value={fullName.lname} />
                <br />
                <button type="submit">Show more</button>

            </form>
        </>

    );
};

export default Home
