import React, {useCallback, useState, useEffect} from 'react'

const App = () => {

  const [passlength, setPassLength] = useState(8);
  const [passchar, setPassChar] = useState(false);
  const [passnum, setPassNum] = useState(false);
  const [password, setPassword] = useState('');

  const passwordGenerator = useCallback(()=>{
let pass = ''
let str = "ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz"
if(passchar) str+= '0123456789'
if(passnum) str+= '`~!@#$%^&*()-_=+[{]}'

for (let i = 0; i <= Array.length; i++) {
  let char = Math.floor(Math.random() * str.length + 1)
  pass += str.charAt(char)
}
setPassword(pass)

  }, [passlength, passchar, passnum, setPassword]);

  useEffect(()=>{
    passwordGenerator();
  }, [passlength, passchar, passnum, passwordGenerator])
  return (
    <>
    <h1 style={{textAlign:'center'}}>Password Generator</h1>
    <input type="text" placeholder='choose the password' value={password} readOnly/>
    <button>Copy</button>
    <input type="range" min={4} max={30} onChange={(e)=> setPassLength(e.target.value)}/>
    <label htmlFor='length'>Length: {passlength}</label>
    <input type="checkbox" defaultChecked={passchar} id="numberCharacter"onChange={()=>setPassChar((prev) => !prev)}/>
    <label>Characters</label>
    <input type="checkbox" defaultChecked={passnum} id="numberInput" onChange={()=>setPassNum((prev) => !prev)}/>
    <label>Numbers</label>
    </>
  )
}

export default App
