import React, {useState} from 'react';
import Tdatas from './Tdatas.json';

const Tdata = () => {

const [nData,setnData] = useState(Tdatas);

const handleRemove = (ItemId) => {

setnData(nData.filter(item => item.id!== ItemId))

}

const handleUpdate = () => {

    setnData(nData.map(items => {

        if (items.id === ItemId){
            return {name: "newName"}
        }
        else{
            return items;
        }
    }))
}
return(
<>
<ul>
{

nData.map(items => 
<li key = {items.id}>
 {items.name} 
<br/>
<button onClick = {() => handleUpdate(items.id)}>Update</button>  

<button onClick = {() => handleRemove(items.id)}>Remove</button>  
</li>)
}


</ul>
<button onClick = {() => setnData([])}>Clear All</button>
</>
)

}
export default Tdata
